import {getUser} from '../services/api.js';
import { useEffect,useState } from 'react';





const AllUser = () => {

    const [users, setUsers] = useState({
        name:'',
        username:'',
        email:'',
        phone:''
    });

    useEffect(()=>{
        getAllUser();
    },[]);
    const getAllUser = async () => {
        let allUser = await getUser();
        setUsers({...users, name:allUser.name,username:allUser.username,email:allUser.email,phone:allUser.email});
        console.log("Aluser",allUser);
        console.log("usres",users);       
        
    }

    return(
       
        
        <table className="table table-dark">
            <thead>
                <tr>
                    <th scope="col">#</th>
                    <th scope="col">Name</th>
                    <th scope="col">Username</th>
                    <th scope="col">Email</th>
                    <th scope="col">Phone</th>
                </tr>
            </thead>
            <tbody>
            
                    {
                        users.map((user,index)=>{
                            <tr>
                            <td>{user[index].name}</td>
                            </tr>
                        })
                       
                    }
                    {/* <tr>
                        <td>
                            {users[0].name}
                        </td>
                    </tr> */}

                    {/* <th sope="row">1</th>
                    <td></td>
                    <td>Otdfo</td>
                    <td>@fdo</td> */}
                               
            </tbody>
           
        </table>
        
    )
}
export default AllUser
